import React,{useState} from "react";
import Footer from "./Footer";
import Header from "./Header";
import Search from "./Search";
import Blog from "./Blog";
import CreateBlog  from "./CreateBlog";
import preblog from "./info";
import Entry from "./Entry";
import Filtered from "./Filtered";
function App(){

    const [blogs, setBlogs] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");
    function addBlog(newBlog) {
        setBlogs((prevBlogs) => {
          return [...prevBlogs, newBlog];
        });
      }

  // const [filteredPosts, setFilteredPosts] = useState([]);
  // const handleTitleSearch = (query) => {
  //   const filtered = blogs.filter((post) =>
  //     post.title.toLowerCase().includes(query.toLowerCase())
  //   );
  //   setFilteredPosts(filtered);
  // };
  // const [filteredPreblogs, setFilteredPreblogs] = useState([]);
  // const handleTitleSearch = (query) => {
  //   const filtered = preblog.filter((blog) =>
  //     blog.title.toLowerCase().includes(query.toLowerCase())
  //   );
  //   setFilteredPreblogs(filtered);
  // };

  const handleTitleSearch = (query) => {
    setSearchQuery(query);
  };
  const filteredBlogs = blogs.filter((blog) =>
  blog.title.toLowerCase().includes(searchQuery.toLowerCase())
);

  const filteredPreBlogs = blogs.filter((blog) =>
    blog.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

    return(
        <div>  
            <Header/>
            <Search onSearch={handleTitleSearch} />


            <Filtered preblog={preblog} />
            <CreateBlog onAdd={addBlog} />

          {/* Displaying filtered blogs if there's a search query */}

        {filteredBlogs.map((blog, index) => (
            <Blog
              imgURL={blog.imgURL}
              key={index}
              id={index}
              title={blog.title}
              content={blog.content}
              theme={blog.theme}
              description={blog.description}
            />
          ))}


      <dl className="dictionary">{preblog.map(Entry)}</dl>      
            <Footer />
        </div>   
    );
}

export default App;
