import React, { useState } from "react";
import Footer from "./Footer";
import Header from "./Header";
import Search from "./Search";
import Blog from "./Blog";
import CreateBlog from "./CreateBlog";
import preblog from "./info";
import Filtered from "./Filtered";

function App() {
  const [blogs, setBlogs] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [filteredTheme, setFilteredTheme] = useState("");

  function addBlog(newBlog) {
    setBlogs((prevBlogs) => {
      return [...prevBlogs, newBlog];
    });
  }

  const handleTitleSearch = (query) => {
    setSearchQuery(query.toLowerCase());
  };

  const handleThemeFilter = (theme) => {
    setFilteredTheme(theme.toLowerCase());
  };

  const filteredPreblogs = preblog.filter(
    (blog) =>
      (searchQuery === "" || blog.title.toLowerCase().includes(searchQuery)) &&
      (filteredTheme === "" || blog.theme.toLowerCase().includes(filteredTheme))
  );

  return (
    <div>
      <Header />
      <Search onSearch={handleTitleSearch} />
      <CreateBlog onAdd={addBlog} />
      <Filtered preblog={preblog} onThemeFilter={handleThemeFilter} />
      {/* Displaying filtered preblogs based on the search query and theme filter */}
      {filteredPreblogs.map((blog, index) => (
        <Blog
          imgURL={blog.imgURL}
          key={index}
          id={index}
          title={blog.title}
          content={blog.content}
          theme={blog.theme}
          description={blog.description}
        />
      ))}

      {/* Displaying user-added blogs */}
      {blogs.map((blog, index) => (
        <Blog
          imgURL={blog.imgURL}
          key={index}
          id={index}
          title={blog.title}
          content={blog.content}
          theme={blog.theme}
          description={blog.description}
        />
      ))}





      <Footer />
    </div>
  );
}

export default App;
