import React, { useState } from "react";
import preblog from "./info"; // Assuming info.js is in the same directory

const [searchTerm, setSearchTerm] = useState("");
const [foundBlog, setFoundBlog] = useState(null);
const handleSearch = (query) => {
  setSearchTerm(query);
  const found = preblog.find((blog) =>
    blog.title.toLowerCase() === query.toLowerCase()
  );
  setFoundBlog(found);
};
const BlogList = ({ blog }) => {
  return (
    <div>
      <h2>{blog.title}</h2>
      <img src={blog.imgURL} alt={blog.title} />
      <p>{blog.content}</p>
      {/* Add other blog details here */}
    </div>
  );
};

const SearchBar = ({ handleSearch }) => {
  return (
    <input
      type="text"
      placeholder="Search by title..."
      onChange={(e) => handleSearch(e.target.value)}
    />
  );
};
export default SearchBar;
