import React, { useState } from "react";
import Footer from "./Footer";
import Header from "./Header";
import Search from "./Search";
import Blog from "./Blog";
import CreateBlog from "./CreateBlog";
import preblog from "./info";

function App() {
  const [blogs, setBlogs] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [filteredPreblogs, setFilteredPreblogs] = useState([]);

  function addBlog(newBlog) {
    setBlogs((prevBlogs) => {
      return [...prevBlogs, newBlog];
    });
  }

  const handleTitleSearch = (query) => {
    setSearchQuery(query.toLowerCase());
    const filtered = preblog.filter((blog) =>
      blog.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredPreblogs(filtered);
  };

  return (
    <div>
      <Header />
      <Search onSearch={handleTitleSearch} />

      {/* Displaying preblogs based on the search query */}
      {filteredPreblogs.map((blog, index) => (
        <Blog
          imgURL={blog.imgURL}
          key={index}
          id={index}
          title={blog.title}
          content={blog.content}
          theme={blog.theme}
          description={blog.description}
        />
      ))}

      {/* Displaying user-added blogs */}
      {blogs.map((blog, index) => (
        <Blog
          imgURL={preblog.imgURL}
          key={index}
          id={index}
          title={blog.title}
          content={blog.content}
          theme={blog.theme}
          description={blog.description}
        />
      ))}

      <CreateBlog onAdd={addBlog} />
      <Footer />
    </div>
  );
}

export default App;
